import './Tasks.css'
import { useEffect, useState } from "react"
import { Link } from 'react-router-dom'
import { get, del, backgrounds, deleteAnim } from '../Tools/Utils/Utils'

const Tasks = () => {

const [tasks,setTasks] = useState(null)
const [task,viewTask] = useState({ })

const displayTasks = () => {
	setInterval(
	get(`/db/tasks`).then(tasks => setTasks(tasks.map(task => { return <Task key={ task._id } data={ task } onClick={ () => viewTask(task) }/> }))),5000)
}

useEffect(displayTasks, [])

const Task = ({ data, onClick }) => {

	const [style, setStyle] = useState({ })

	return (
		<div className='Task' style={{ backgroundColor: backgrounds[data.status]??`var(--paleOrange)`, ...style }}>
			<button className='Delete' onClick={() => del(`/db/tasks/${data._id}`, () => setStyle(deleteAnim))}></button>
			<div onClick={ onClick }>
			<p className='Title'>{ data.title }</p>
			<p className='Id'>{ data._id }</p>
			<p className='TaskTool'>{ data.toolname }</p>
			<p className='CreatedAt'>{ data.createdAt }</p>
			</div>
		</div>
	)
}

const TaskInfo = ({ data }) => {
	return (
		<div className='TaskInfo'>
			<p className='Title'>{ data.title??`Task view` }</p>
			<p className='Status'>{ data.status }</p>
			<p className='id'>{ data._id }</p>
			<p className='TaskTool'>{ data.toolname }</p>
			<p className='TaskDir'>{ data.dir }</p>
			{ (data.status === 'done')?<Link to={`/Files/${data.toolname}/${data.title}`}>Results</Link>:<p>No results avaliable</p> }
		</div>
	)
}

	return (
		<div className='Divider'>
		<div className='TaskList'>{ tasks }</div>
		<TaskInfo data={ task }/>
		</div>
	)
}

export default Tasks
