import './Tasks.css'
import Task from './Task/task'
import TaskInfo from './TaskInfo/TaskInfo'
import axios from 'axios'
import React from "react"

const Tasks = () => {

React.useEffect(() => {
	displayTasks()
}, [])

	const displayTasks = () => {
		axios.get('http://192.168.1.37:8080/db').then(res => {
			setTasks(res.data.map(task =>{return <Task key={task._id} data={task} onClick={() => { viewTask(task) }} />}))
			})
		}

	const [tasks,setTasks] = React.useState(null)
	const [task,viewTask] = React.useState(null)

	return (
		<div className='Divider'>
		<div className='TaskList'>{tasks}</div>
		<TaskInfo data={task}/>
		</div>
	)
}

export default Tasks
