import './Files.css'
import { File, Dir, Preview } from './File/File'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'

const Files = () => {
	const path = useResolvedPath().pathname
	const [file, viewFile] = useState(`choose file to preview`)

	const getPreview = (path) => {
		axios.get(`http://192.168.1.37:8080${path}`).then(file => viewFile(file.data))
	}

	const getFiles = () => {
		axios.get(`http://192.168.1.37:8080${path}`).then(files => {
			return(files.data.map(file => { 
				return (file.isDir?<Dir data={file} />:<File data={file} />) })
			)
		})
	}

	const [files, setFiles] = useState (null)

	return (
		<div className='Divider'>
			<div className='Files'>{files}</div>
			<div>{file}</div>
		</div>
	)
}

export default Files
