import './Tasks.css'
import axios from 'axios'
import { useEffect, useState } from "react"
import { Link } from 'react-router-dom'
import { getCollection } from '../Tools/Utils/Utils'

const Tasks = () => {

const Task = ( {data, onClick} ) => {

	const backgrounds = {
		queued: `var(--paleBlue)`,
		done:`var(--paleGreen)`,
		running:`var(--background)`
	}

	const [style, setStyle] = useState({backgroundColor: backgrounds[data.status]??"var(--paleRed)"})

	const deleteAnim = {
		transform: `translateX(-110%)`,
		marginBottom: `-120px`,
		transition: `transform .6s, margin-bottom .6s`
	}

	const deleteTask = ({ _id }) => {
		axios.delete(`http://192.168.1.37:8080/db/tasks/${_id}`).then(() => setStyle( deleteAnim ))
	}

	return (
		<div className='Task' style={style}>
			<button className='Delete' onClick={() => deleteTask(data)}></button>
			<div onClick={ onClick }>
			<p className='Title'>Title: {data.title}</p>
			<p className='Id'>{data._id}</p>
			<p className='TaskTool'>{data.toolname}</p>
			<p className='CreatedAt'>{data.createdAt}</p>
			</div>
		</div>
	)
}

//getCollection(`tasks`).map(task => setTasks(<Task key={ task._id } data={ task } onClick={ () => viewTask(task) }/>))

const displayTasks = async () => {
	console.log(await getCollection(`tasks`))
}

useEffect(displayTasks, [])

const TaskInfo = ({ data }) => {
	return (
		<div className='TaskInfo'>
			<p className='Title'>{data.title??`Task view`}</p>
			<p className='Status'>{data.status}</p>
			<p className='id'>{data._id}</p>
			<p className='TaskTool'>{data.toolname}</p>
			<p className='TaskDir'>{data.dir}</p>
			{(data.status==='done')?<Link to={`/Files/${data.toolname}/${data.title}`}>Results</Link>:<p>No results avaliable</p>}
		</div>
	)
}

const [tasks,setTasks] = useState(null)
const [task,viewTask] = useState({})

	return (
		<div className='Divider'>
		<div className='TaskList'>{ tasks }</div>
		<TaskInfo data={ task }/>
		</div>
	)
}

export default Tasks
