import './Tasks.css'
import TaskInfo from './TaskInfo/TaskInfo'
import axios from 'axios'
import { useEffect, useState } from "react"

const Tasks = () => {

	useEffect(() => { displayTasks() }, [])


const Task = ({data, onClick}) => {
	let background
	switch (data.status){
		case `queued`: background = `var(--paleBlue)`; break;
		case `done`: background = `var(--paleGreen)`; break;
		case null: case `errored`: background = `var(--paleRed)`; break;
		case `w/errors`: background = `var(--paleOrange)`; break;
		case `running`: background = `var(--background)`; break;
		default: background = `var(--neutral)`; break;
	}
	const [style, setStyle] = useState({backgroundColor: background})

	const deleteAnim = () => {
		setStyle({ animation:'Deletion .6s', backgroundColor:background})
		setTimeout(() => { setStyle({display:'none'}) }, 400)
	}

	const deleteTask = ({_id}) => {
		axios.delete(`http://192.168.1.37:8080/db/tasks/${_id}`).then(() => deleteAnim())
	}

	return (
		<div className='Task' style={style} onClick={onClick}>
			<button className='Delete' onClick={() => deleteTask(data)}></button>
			<p className='Title'>Title: {data.title}</p>
			<p className='TaslTool'>{data.toolname}</p>
			<p className='TaskDir'>{data.dir}</p>
		</div>
	)
}



	const displayTasks = () => {
		axios.get('http://192.168.1.37:8080/db/tasks').then(res => {
			setTasks(res.data.map(task =>{ return <Task key={task._id} data={task} onClick={() => { viewTask(task) }} />}))
			})
		}

	const [tasks,setTasks] = useState(null)
	const [task,viewTask] = useState(null)

	return (
		<div className='Divider'>
		<div className='TaskList'>{tasks}</div>
		<TaskInfo data={task}/>
		</div>
	)
}

export default Tasks
