import './Files.css'
import { File, Dir } from './File/File'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'
import { useForm } from "react-hook-form";

const FS = () => {

const path = useResolvedPath().pathname
const { register, handleSubmit } = useForm()
const [file, viewFile] = useState()

const uploadFiles = ({ files }) => {

	let form = new FormData()
	Object.keys(files).map(file => form.append("file", files[file]))
	axios.post(`http://192.168.1.37:8080${path}`,
		form, 
		{ headers:
			{ "Content-type":"multipart/form-data" } 
		})
		.then(() => console.log(`Saved`))
		.catch()
}

const New = ({ onClick }) => {
	return (
		<div className='New' onClick={onClick}>
			<form onSubmit={ handleSubmit(data => uploadFiles(data)) }>
				<input { ...register(`files`) } type="file" multiple />
				<input type="submit"/>
			</form>
			<p className='Filename'>Add new</p>
		</div>
	)
}

const setPreview = (file) => {
	axios.get(`http://192.168.1.37:8080/files${file.path}`)
		.then(res => viewFile({data: res.data, info: file}))
}

const Preview = () => {


	return (
		<div className='Preview'>{file.info.filename}{file.data}</div>
	)
	/*const getData = () => {
		axios.get(`http://192.168.1.37:8080/files${file.path}`)
			.then(res => viewFile(res.data))
			.catch(() => viewFile(`Can not view file`))
	}
	if(file)switch(file.ext){
		case '.png': return(<div className='Preview'><img src={`http://192.168.1.37:8080/files${file.path}`} alt={ `O` } /></div>)
		default: getData(); return(<div className='Preview'>{file.data}</div>)
	}
	else return(<div className='Preview'>Select a file to preview</div>)*/
}

const Files = () => {

	const [files, setFiles] = useState (null)

	useEffect(() => {
		axios.get(`http://192.168.1.37:8080${path}`).then(res => {
			setFiles(res.data.map(file => {
				return file.isDir?<Dir key={file.filename} data={file}/>:<File key={file.filename} data={file} onClick={() => setPreview(file)}/> 
			}))
		})
	}, [])

	return (
		<div className='Divider' style={{gridTemplateColumns:`60% 40%`}}>
		<div className='Wrapper'>
			<div className='FSnav'>
				<div className='PathSelector'>{path}</div>
			</div>
			<div className='Files'><New onClick={() => { console.log(`Add New`) }} />{files}</div>
		</div>
			<Preview file={file} />
		</div>
		)
	}

	return (<Files />)

}

export default FS
