import './File.css'
import { Link } from 'react-router-dom'
import axios from 'axios'
import {useState} from 'react'

export const File = ({ data, onClick }) => {
	return (
		<div className='File' onClick={onClick}>
			{ data.filename }
		</div>
	)
}

export const Dir = ({ data }) => {
	return (
		<div className='Dir'>
			<Link to={`/Files${data.path}`}>{data.filename}</Link>
		</div>
	)
}

export const Preview = ({ file }) => {

	const [data, setData] = useState(null)

	const getData = () => {
		axios.get(`http://192.168.1.37:8080/files${file.path}`, { responseType: Blob })
			.then(res => setData(res.data))
			.catch(() => setData(`Can not view file`))
	}

	if (file.size < 200000) getData()
	if(file)switch(file.ext){
		case '.png': return(<img src={`http://192.168.1.37:8080/files${file.path}`} alt={ file.filename } />)
		default: getData(); return(<>{data}</>)
	}
	else return(<>Select a file to preview</>)
}
