import './task.css'

import axios from 'axios'

const Task = ({data, onClick}) => {

	let style = {
		backgroundcolor: 'var(--paleBlue)'
	}

	const deleteTask = ({_id}) => {
		axios.delete(`http://192.168.1.37:8080/db/tasks/${_id}`).then(() => style = {display:'none'})
	}

	return (
		<div className='Task' style={style} onClick={onClick}>
			<span className='Title'>Title:</span>	{data.title}
			<button className='Delete' onClick={() => deleteTask(data)}>
			</button>
		</div>
	)
}

export default Task
