import axios from 'axios'
import { useEffect, useState } from 'react'
import { DbBackgrounds } from '../Tools/Utils/Utils'
import './Databases.css'

const Databases = () => {

const DbEntry = ({ data, Db}) => {

	const background = DbBackgrounds.get(data.type)??"var(--paleRed)"

	const [style, setStyle] = useState({ })

	const deleteAnim = () => {
		setStyle({ animation:'Deletion .6s', backgroundColor:background})
		setTimeout(() => { setStyle({display:'none'}) }, 400)
	}

	const delOne = (Db, id) => {
	axios.delete(`http://192.168.1.37:8080/db/${Db}/${id}`).then(() => deleteAnim())
	}

	return (
		<div className='Entry' style={{ background: DbBackgrounds[data.type], style}}>
			<button className='Delete' onClick={() => delOne(Db, data._id) }></button>
			<p className='Title'>{data.title}</p>
			<p className='Type'>{data.type}</p>
			{data._id}
		</div>
	)
}

const [blastDb, setBlastDb] = useState(null)
const [skaniSketch, setSkani] = useState(null)

const getMany = (Db, hook) => {
	axios.get(`http://192.168.1.37:8080/db/${Db}`).then(res => { hook(res.data.reverse().map(entry => { return <DbEntry key={ entry._id } data={ entry } Db={ Db }/> })) })
}

const getDbs = () => {
	getMany(`blast-db`, setBlastDb)
	getMany(`skani-sketch`, setSkani)
}

useEffect(getDbs,[])

	return (
		<div className='DatabaseList'>
			<div className='DbWrapper'><div className='DbTitle'>Blast</div><div className='Db'>{blastDb}</div></div>
			<div className='DbWrapper'><div className='DbTitle'>Skani</div><div className='Db'>{skaniSketch}</div></div>
		</div>
	)
}

export default Databases
