import './Files.css'
import { useEffect, useRef, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom'
import { saveAs } from 'file-saver'

const FS = () => {

const path = useResolvedPath().pathname

const Files = () => {

const preview = useRef({data:"",info:""})
const { register, handleSubmit } = useForm()

const [files, setFiles] = useState (null)

const getFiles = () => {
	axios.get(`http://192.168.1.37:8080${path}`).then(res => {
		setFiles(res.data.map(file => {
			return file.isDir?<Dir key={file.filename} data={file}/>:<File key={file.filename} data={file}/> 
		}))
	})
}

useEffect(getFiles, [])

const download =(path, name) => {
	saveAs(`http://192.168.1.37:8080/download${path}`, name)
}

const remove  = (path) => {
	axios.delete(`http://192.168.1.37:8080/files${path}`).then(() => getFiles())
}

const File = ({ data }) => {
	return (
		<div className='File'>
			<button className='Delete' onClick={() => { remove(data.path) }} />
			<button className='Download' onClick={() => { download(data.path, data.filename) }} />
			<div onClick={() => preview.current = data}>
				<Info data={data}/>
			</div>
		</div>
	)
}

const Dir = ({ data }) => {
	return (
	<div className='Dir'>
		<button className='Delete' onClick={() => { remove(data.path) }}/>
		<button className='DownloadArch' onClick={() => { download(data.path, `${data.filename}.zip`) }} />
		<Link to={`/Files${data.path}`}>
			<Info data={data}/>
		</Link>
	</div>
	)
}

const Info = ({ data }) => {
	return ( 
		<div className='Info'>
			<p className='Filename'>{data.filename}</p>
			<p className='Ext'>{data.ext?data.ext:`Dir`}</p>
			<p className='CreatedAt'>{Intl.DateTimeFormat().format(new Date(data.createdAt))}</p>
			<p className='Size'>{Intl.NumberFormat({style:`unit`, unit:`byte`}).format(data.size)}B</p>
		</div>
	)
}

const uploadFiles = ({ files }) => {
	let form = new FormData()
	Object.keys(files).map(file => form.append("file", files[file]))
	axios.post(`http://192.168.1.37:8080${path}`,
		form, 
		{ headers:
			{ "Content-type":"multipart/form-data" } 
		})
		.then(() => getFiles())
		.catch()
}

const New = ({ onClick }) => {
	return (
		<div className='New' onClick={onClick}>
			<form onSubmit={ handleSubmit(data => uploadFiles(data)) }>
				<input { ...register(`files`) } type="file" multiple />
				<input type="submit"/>
			</form>
			<p className='Filename'>Add new</p>
		</div>
	)
}

const Preview = () => {

	const [data, setPreview] = useState(preview)

	const previewFile = (file) => {
		if(!file.path) return
		axios.get(`http://192.168.1.37:8080/files${file.path}`)
		.then(res => setPreview({data: res.data, info: file}))
		.catch(() => setPreview({data: `Can not preview`, info:``}))
	}

	useEffect(() => previewFile(preview), [])

	return (
		<div className='Preview'>{data.info}<br />{data.data}</div>
	)
}

	return (
		<div className='Divider' style={{gridTemplateAreas: `80% 20%`}} >
		<div className='Wrapper'>
			<div className='FSnav'>
				<div className='PathSelector'>{path}</div>
			</div>
			<div className='Files'><New onClick={() => { console.log(`Add New`) }} />{files}</div>
		</div>
			<Preview filePreview={ preview } />
		</div>
		)
	}

return (
	<Files />
)

}

export default FS
