import './task.css'

import axios from 'axios'
import {useState} from 'react'

const Task = ({data, onClick}) => {
	const background = 'var(--paleBlue)'
	const [style, setStyle] = useState({backgroundColor: background})

	const deleteAnim = () => {
		setStyle({ animation:'Deletion .7s', backgroundColor:background})
		setTimeout(() => { setStyle({display:'none'}) }, 700)
	}

	const deleteTask = ({_id}) => {
		axios.delete(`http://192.168.1.37:8080/db/tasks/${_id}`).then(() => deleteAnim())
	}

	return (
		<div className='Task' style={style} onClick={onClick}>
			<span className='Title'>Title:</span>	{data.title}
			<button className='Delete' onClick={() => deleteTask(data)}>
			</button>
		</div>
	)
}

export default Task
