import axios from "axios"
import { Link } from "react-router-dom"

export const backgrounds = {
		queued: `var(--paleBlue)`,
		done:`var(--paleGreen)`,
		running:`var(--background)`
}

export const deleteAnim = {
		transform: `translateX(-110%)`,
		marginBottom: `-120px`,
		transition: `transform .6s, margin-bottom .6s`
}

export const ProgressBar = ({ progress }) => {
	return (
		<div className='UploadBar'><progress max="100" value={ progress }/></div>
	)
}

export const getCollection = ( collection, hook, Component ) => {
	axios.get(`http://192.168.1.37:8080/db/${collection}`).then(res => { hook(res.data.reverse().map(data => { return <Component data={ data }/> })) })
}

export const sendForm = ( data, path, hook, onDone, onError) => {
	axios.post(`http://192.168.1.37:8080/${path}`,
		data,
		{ headers: { "Content-type":"multipart/form-data" },
			onUploadProgress: ProgressEvent => { hook(Math.floor((ProgressEvent.loaded * 100) / ProgressEvent.total)) }
		})
		.then(onDone())
		.catch(err => onError())
}

export const DownloadLink = ({ path, filename }) => { return (
		<Link to={`http://192.168.1.37:8080/download${path}`} className='Download' target="_blank" download={ filename } />)
}
