import './Tasks.css'
import { useEffect, useState } from "react"
import { Link } from 'react-router-dom'
import { get, del, backgrounds, deleteAnim } from '../Utils/Utils'

const Tasks = () => {

const [running,setRunning] = useState([])
const [completed,setCompleted] = useState([])
const [queued, setQueued] = useState([])

const displayTasks = () => {
const set = {
	"done": setCompleted,
	"errored": setCompleted,
	"queued": setQueued,
	"running": setRunning
}

	get(`/db/tasks`).then(tasks => tasks.forEach(task => { setCompleted(...<Task key={ task._id } data={ task } />) }))
}

useEffect(displayTasks, [])

const Task = ({ data, onClick }) => {

	const [style, setStyle] = useState({ })

	return (
		<div className='Task' style={{ backgroundColor: backgrounds[data.status]??`var(--paleOrange)`, ...style }}>
			<button className='Delete' onClick={() => del(`/db/tasks/${data._id}`, () => setStyle(deleteAnim))}></button>
			<div onClick={ onClick }>
			<p className='Title'>Title: { data.title } | { data.toolname }</p>
			<p className='Id'>{ data._id }</p>
			<p className='CreatedAt'>{ data.createdAt }</p>
			</div>
		</div>
	)
}

const TaskInfo = ({ data }) => {
	return (
		<div className='TaskInfo'>
			<p className='Title'>{ data.title??null} { (data.status === 'done')?<Link to={`/Files/${data.toolname}/${data.title}`}>Results</Link>:`No results avaliable` }</p>
			<p className='Status'>{ data.status }</p>
			<p className='id'>{ data._id }</p>
			<p className='TaskTool'>{ data.toolname }</p>
			<p className='TaskDir'>{ data.dir }</p>
		</div>
	)
}

	return (
		<div className='Form'>
		<div className='DbWrapper'><div className='TaskList'>{ completed }</div></div>
		<div className='DbWrapper'><div className='TaskList'>{ running }</div></div>
		<div className='DbWrapper'><div className='TaskList'>{ queued }</div></div>

		</div>
	)
}

export default Tasks
