import './Files.css'
import { File, Dir, Preview } from './File/File'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'

const Files = () => {
	const path = useResolvedPath().pathname
	const [files, setFiles] = useState(`No files`)
	const [file, viewFile] = useState(`choose file to preview`)

	const getPreview = (path) => {
		axios.get(`http://192.168.1.37:8080${path}`).then(file => viewFile(file))
	}

	const getFiles = () => {
		axios.get(`http://192.168.1.37:8080${path}`).then(files => {
			setFiles(files.data.map(file => { 
				return (file.isDir?<Dir />:<File/>) })
			)
		})
	}
	useEffect(() => getFiles())
	return (
		<div className='Divider'>
			<>{files}</>
			<Preview data={file}/>
		</div>
	)
}

export default Files
