import './Files.css'
import { File, Dir, Preview } from './File/File'
import { useEffect, useReducer, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'
import { useForm } from "react-hook-form";

const New = ({ onClick }) => {
	return (
		<div className='New' onClick={onClick}>
			<p className='Filename'>Add new</p>
		</div>
	)
}

const UploadForm = (style) => {

	const { upload, handleSubmit } = useForm()

	return (
		<div className='Upload' style={style}>
			<form onSubmit={handleUpload}>
				<input
					type= { `text` }
					onChange={setFormData.path}/>
				<input
					type= { `text` }
					onChange={setFormData.data}/>
				<input
					type= { `file` }
					id= { `files` }
					multiple />
				<input type= { `submit` } />
			</form>
		</div>
	)
}

const Files = () => {
	const path = useResolvedPath().pathname
	const [file, viewFile] = useState(``)

	const [files, setFiles] = useState (null)

	useEffect(() => {
		axios.get(`http://192.168.1.37:8080${path}`).then(res => {
			setFiles(res.data.map(file => {
				return file.isDir?<Dir key={file.filename} data={file}/>:<File key={file.filename} data={file} onClick={() => viewFile(file)}/> 
			}))
		})
	}, [path])

	return (
		<div className='Divider' style={{gridTemplateColumns:`60% 40%`}}>
		<div className='Wrapper'>
			<div className='FSnav'>
				<div className='PathSelector'>{path}<UploadForm /></div>
			</div>
			<div className='Files'><New onClick={() => { console.log(`Add New`) }} />{files}</div>
		</div>
			<Preview file={file} />
		</div>
	)
}

export default Files
