import './Files.css'
import { File, Dir, Preview } from './File/File'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'
import { useForm } from "react-hook-form";

const FS = () => {


const Files = () => {

const path = useResolvedPath().pathname
const { register, handleSubmit } = useForm
const [ file, previewFile ] = useState()

const [files, setFiles] = useState (null)

const getFiles = () => {
	axios.get(`http://192.168.1.37:8080${path}`).then(res => {
		setFiles(res.data.map(file => {
			return file.isDir?<Dir key={file.filename} data={file}/>:<File key={file.filename} data={file} onClick={() => previewFile(file)}/> 
		}))
	})
}

useEffect(getFiles, [setFiles])

const uploadFiles = ({ files }) => {

	let form = new FormData()
	Object.keys(files).map(file => form.append("file", files[file]))
	axios.post(`http://192.168.1.37:8080${path}`,
		form, 
		{ headers:
			{ "Content-type":"multipart/form-data" } 
		})
		.then(() => getFiles())
		.catch()
}

const New = ({ onClick }) => {
	return (
		<div className='New' onClick={onClick}>
			<form onSubmit={ handleSubmit(data => uploadFiles(data)) }>
				<input { ...register(`files`) } type="file" multiple />
				<input type="submit"/>
			</form>
			<p className='Filename'>Add new</p>
		</div>
	)
}

	return (
		<div className='Divider' style={{gridTemplateColumns:`60% 40%`}}>
		<div className='Wrapper'>
			<div className='FSnav'>
				<div className='PathSelector'>{path}</div>
			</div>
			<div className='Files'><New onClick={() => { console.log(`Add New`) }} />{files}</div>
		</div>
			<Preview filePreview={ file } />
		</div>
		)
	}

	return (<Files />)

}

export default FS
