import './Files.css'
import { File, Dir, Preview } from './File/File'
import { useEffect, useReducer, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'
import { useForm } from "react-hook-form";

const path = useResolvedPath().pathname

const handleSubmit = (data) => {
	let form = new FormData()
	(data.file).map(file => form.append(file))
	axios.post(`http://192.168.1.37:8080/files${path}`, form, { headers: { "Content-type":"multipart/form-data" } })
}


const New = ({ onClick }) => {
	return (
		<div className='New' onClick={onClick}>
			<p className='Filename'>Add new</p>
		</div>
	)
}

const UploadForm = (style) => {

	const { register, handleSubmit } = useForm()

	return (
		<div className='Upload' style={style}>
			<form onSubmit={handleSubmit((data) => { let form = new FormData(); form.append("file", data.file[0]); form.append("file", data.file[1])
						axios.post(`http://192.168.1.37:8080/files/`, form, 
						{headers: {"Content-Type":`multipart/form-data`}})})}>
				<input {...register("data")} placeholder="First name" />
				<input {...register(`file`)} type="file" multiple />
				<input type="submit" />
			</form>
		</div>
	)
}

const Files = () => {
	const path = useResolvedPath().pathname
	const [file, viewFile] = useState(``)

	const [files, setFiles] = useState (null)

	useEffect(() => {
		axios.get(`http://192.168.1.37:8080${path}`).then(res => {
			setFiles(res.data.map(file => {
				return file.isDir?<Dir key={file.filename} data={file}/>:<File key={file.filename} data={file} onClick={() => viewFile(file)}/> 
			}))
		})
	}, [path])

	return (
		<div className='Divider' style={{gridTemplateColumns:`60% 40%`}}>
		<div className='Wrapper'>
			<div className='FSnav'>
				<div className='PathSelector'>{path}<UploadForm /></div>
			</div>
			<div className='Files'><New onClick={() => { console.log(`Add New`) }} />{files}</div>
		</div>
			<Preview file={file} />
		</div>
	)
}

export default Files
