import './Files.css'
import File from './File/File'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { parsePath, useResolvedPath, Link } from 'react-router-dom'

const Files = () => {
	useEffect(() => getFiles(), [])
	const path = useResolvedPath().pathname
	const [files, setFiles] = useState(`files`)
	const getFiles = () => {
		axios.get(`http://192.168.1.37:8080${path}`).then(files => {
			files.map(file => setFiles(file.isDir?<Link to={`/Files/${file.path}`}>file.filename</Link>:<div>file.filename</div>)
			)
		})
	}

	return (
		<>{files}</>
	)
}

export default Files
