import './File.css'
import { Link } from 'react-router-dom'
import axios from 'axios'
import {useState} from 'react'
import { saveAs } from 'file-saver'

const download =(path, name) => {
	saveAs(`http://192.168.1.37:8080/download${path}`, name)
}

const [style, setStyle] = useState(null)

const deleteAnim = () => {
	setStyle({ animation:'Deletion .6s', backgroundColor:background})
	setTimeout(() => { setStyle({display:'none'}) }, 400)
}

const remove  = (path) => {
	axios.delete(`http://192.168.1.37:8080/files${path}`)
		.then(() => deleteAnim())
		.catch(() => errorAnim())
}

export const File = ({ data, onClick }) => {
	return (
		<div className='File'>
			<button className='Delete' onClick={() => { remove(data.path) }} />
			<button className='Download' onClick={() => { download(data.path, data.filename) }} />
			<div onClick={onClick}>{data.filename}</div>
		</div>
	)
}

export const Dir = ({ data }) => {
	return (
	<div className='Dir'>
		<button className='Delete' onClick={() => { remove(data.path) }}/>
		<button className='DownloadArch' onClick={() => { download(data.path, `${data.filename}.zip`) }} />
		<Link to={`/Files${data.path}`}>{data.filename}</Link>
	</div>
	)
}

export const Preview = ({ file }) => {

	const [data, setData] = useState(null)

	const getData = () => {
		axios.get(`http://192.168.1.37:8080/files${file.path}`)
			.then(res => setData(res.data))
			.catch(() => setData(`Can not view file`))
	}

	if(file)switch(file.ext){
		case '.png': return(<div className='Preview'><img src={`http://192.168.1.37:8080/files${file.path}`} alt={ file.filename } /></div>)
		default: getData(); return(<div className='Preview'>{data}</div>)
	}
	else return(<div className='Preview'>Select a file to preview</div>)
}
