import './Files.css'
import { useEffect, useRef, useState } from 'react'
import axios from 'axios'
import { useResolvedPath } from 'react-router-dom'
import { useForm } from "react-hook-form";
import { Link } from 'react-router-dom'
import { Buffer } from 'buffer';
import { get, del, DownloadLink } from '../Tools/Utils/Utils';

const FS = () => {

const path = useResolvedPath().pathname
const previewRef = useRef({info:{filename:"AAA"}, data:"aaa"})

const Files = () => {

const { register, handleSubmit } = useForm()

const [files, setFiles] = useState (null)
const [data, setPreview] = useState(previewRef.current)

const getFiles = () => {
	get( path ).then(res => {
		setFiles(res.data.map(file => {
			return file.isDir?<Dir key={file.filename} data={file}/>:<File key={file.filename} data={file}/> 
		}))
	}).catch(() => console.warn(`Directory does not exist`))
}

useEffect(getFiles, [])

const preview = async (file) => {
	let data
	(file.size>1000000)?data = { data:`File too large to preview`, info: file }
	:data = {
		data: ( await get(`/files${file.path}`)
			.catch(() => { return(`Can not preview`) })).data,
		info:file
	}
	previewRef.current = data
	return data
}

const File = ({ data }) => {
	return (
		<div className='File'>
			<button className='Delete' onClick={() => { del(`/files${data.path}`) }} />
			<DownloadLink path={ data.path } />
			<div onClick={() => preview(data).then(res => setPreview(res))}>
				<Info data={ data }/>
			</div>
		</div>
	)
}

const Dir = ({ data }) => {
	return (
	<div className='Dir'>
		<button className='Delete' onClick={() => { del(`/files${data.path}`) }}/>
		<DownloadLink path={ data.path } />
		<Link to={`/Files${data.path}`}>
			<Info data={data}/>
		</Link>
	</div>
	)
}

const Info = ({ data }) => {
	return ( 
		<div className='Info'>
			<p className='Filename'>{data.filename}</p>
			<p className='Ext'>{data.ext?data.ext:`Dir`}</p>
			<p className='CreatedAt'>{Intl.DateTimeFormat().format(new Date(data.createdAt))}</p>
			<p className='Size'>{Intl.NumberFormat({style:`unit`, unit:`byte`}).format(data.size)}B</p>
		</div>
	)
}

const uploadFiles = (files) => {
	axios.post(`http://192.168.1.37:8080${path}`,
		files, 
		{ headers:
			{ "Content-type":"multipart/form-data" } 
		})
		.then(() => getFiles())
		.catch()
}

const New = () => {
	return (
		<div className='New'>
		<form onChange={ handleSubmit(data => uploadFiles(data)) }>
				<input { ...register(`files`) } type="file" multiple />
		</form>
		</div>
	)
}

const Preview = () => {

	const binTo64 = (data) => { return Buffer.from(data, 'binary').toString('base64') }
	const types = {
	"text/html": <iframe title={data.info.title} src={`data:${data.info.mime};base64,${binTo64(data.data)}`}/>,
	"image/png": <img className='PreviewImg' src={`http://192.168.1.37:8080/files/${data.info.path}`} alt={``}/>, //I have to fix this
	"image/svg+xml": <img src={ `data:${data.info.mime};base64,${binTo64(data.data)}` } alt={``}/>
	}

	return (
		<div className='Preview'>
			<div className='FileInfo'>
				<button className='DeletePreview' onClick={() => del(`/files${data.info.path}`)}>Delete</button>
				<DownloadLink path={ `/files${data.info.path}` } />
				<p className='Filename'>{data.info.filename}</p>
				<p className='CreatedAt'>{data.info.createdAt}</p>
			</div>
			<div className='FileData'>{types[data.info.mime]??<code>{data.data}</code>}</div>
		</div>
	)
}

	return (
		<div className='Divider' style={{gridTemplateColumns:"60% 40%"}} >
		<div className='Wrapper'>
			<div className='FSnav'>
				<div className='PathSelector'>
					{path}
				</div>
			</div>
			<div className='Files'><New />{files}</div>
		</div>
			<Preview filePreview={ preview } />
		</div>
		)
	}

return (
	<Files />
)

}

export default FS
