import './Files.css'
import { File, Dir } from './File/File'
import { useEffect, useState } from 'react'
import axios from 'axios'
import { parsePath, useResolvedPath, Link } from 'react-router-dom'

const Files = () => {
	const path = useResolvedPath().pathname
	const [files, setFiles] = useState(`files`)
	const getFiles = () => {
		axios.get(`http://192.168.1.37:8080${path}`).then(files => {
			setFiles(files.data.map(file => { 
				return (file.isDir?<Dir />:<File />) })
			)
		})
	}
	useEffect(() => getFiles())
	return (
		<>{files}</>
	)
}

export default Files
