import './File.css'
import { Link } from 'react-router-dom'
import axios from 'axios'
import {useState} from 'react'

export const File = ({ data, onClick }) => {
	return (
		<div className='File' onClick={onClick}>
			{ data.filename }
		</div>
	)
}

export const Dir = ({ data }) => {
	return (
		<div className='Dir'>
			<Link to={`/Files${data.path}`}>{data.filename}</Link>
		</div>
	)
}

export const Preview = ({ file }) => {

	const restricted = [ `.png`, `.jpeg`, `.jpg` ]

	const [data, setData] = useState(`Can not preview file`)

	const getData = () => {
		axios.get(`http://192.168.1.37:8080/files${file.path}`).then(res => setData(res.data))
	}

	if(file && !restricted.includes(file.extname) && file.size < 1500000) { getData() }

	return(<>{data}</>)
}
